                      dbo.POS_M_Orders ON dbo.POS_M_OrderDetails.OrderID = dbo.POS_M_Orders.OrderID AND 
                      dbo.POS_M_OrderDetails.BranchID = dbo.POS_M_Orders.BranchID INNER JOIN
