                      dbo.POS_M_Orders.Status = 1 OR
                      dbo.POS_M_Orders.Status = 2) AND (dbo.POS_M_Orders.Voided = 0) AND (dbo.POS_M_Orders.EODDate IS NULL) AND 
                      (dbo.POS_M_OrderDetails.Printed = 0)
