@ItemID int
AS
SELECT HAPPY_HOUR.Day,DayDescription ,PRICEMODE,Starts,ENDS,HAPPY_HOUR.MenuID ,Menu,SalesItemID, BOF_M_ItemSalesItems.Description,
(case when PRICEMODE=1 then isnull(PriceMode1,0)+isnull(taxmode1,0)
