OrderID=@NewOrderID
END

UPDATE [dbo].[POS_M_OrderDiscounts] SET [OrderId]= @NewOrderID
WHERE OrderID=@OldOrderID

if (@NewOrderNumber <> @OldOrderNumber) 
begin
DELETE FROM [dbo].[POS_M_Orders] WHERE OrderID=@OldOrderID
end
END

' 
