             orderid, 
             menu, 
             Cast(tablenumber AS VARCHAR(50)) AS tablenumber, 
             branch,
             phone, 
             server, 
             closer, 
             printingtime, 
             checknumber, 
             Sum(CONVERT(DECIMAL(18,2),itemprice))itemprice, 
             resturantname, 
             roomcharge, 
             invoiceremark, 
             driver, 
             customeraddress, 
             customername, 
			 isnull((SELECT isnull(rate,0) FROM bof_l_taxes WHERE taxid = 1 AND inclusive = 1), 0) as taxRate1,
